import React, { useState, useEffect } from "react";

const ApiDataReq = () => {
  const [hasError, setErrors] = useState(false);
  const [ApiDataReq, setApiDataReq] = useState({});

  async function fetchData() {
      const res = await fetch("http://localhost:51574/api",{
      mode: 'no-cors',
      content: 'json/text'
    }
    
    );
    res
      .json()
      .then(res => setApiDataReq(res))
      .catch(err => setErrors(err));
  }
  
 useEffect(() => {
    fetchData();
  });
  console.log(ApiDataReq);
  return (
    <div>
      <span>
        {
                    JSON.stringify(ApiDataReq)
        }
      </span>
      <hr />
      <span>Has error: {JSON.stringify(hasError)}</span>
    </div>
  );
};
export default ApiDataReq;
